```
        const circs = (/* page,*/ size, clx, cly, x, y, strk, fil, wl, wh, space/*, what */) => {
            for (let i = 0; i < wl; i += size * space) {
                for (let j = 0; j < wh; j += size * space) {
                    let circle = document.createElementNS("http://www.w3.org/2000/svg", "circle");

                    circle.setAttribute("cx", clx + i);
                    circle.setAttribute("cy", cly + j);
                    circle.setAttribute("r", size);
                    circle.setAttribute("stroke", strk);
                    circle.setAttribute("fill", fil);

                    svg.setAttribute("viewBox", "0 0 600 500");
                    svg.style.position = "absolute";
                    svg.style.top = x;
                    svg.style.left = y;
                    svg.appendChild(circle);
                }
            }

            //page.appendChild(svg); 
            //what;
            document.getElementById("homepage").appendChild(svg)
        };
